import React from "react";
import Products from "./Products.json";

export default class ProdList extends React.Component {
  state = {
    type: "Base",
    filterText: "",
  };

  //UseState is a react hook that lets you set some state to react component.

  myFilter(type) {
    return () => {
      this.setState({ type });
    };
  }

  onChange(e) {
    this.setState({ filterText: e.target.value });
  }

  normalizeString(str) {
    return str.toLowerCase().replace(/\s/g, "");
  }

  render() {
    return (
      <section class="products">
        <h1>Our Products</h1>
        <div class="container">
          <br />
          <section class="sideoptions">
            <div>
              <ul>
                <li>
                  <label class="searchlabel" for="searchbar">
                    Search by name
                  </label>
                </li>
                <li>
                  <input
                    id="searchbar"
                    name="searchbar"
                    placeholder=" Ex:(product name)"
                    type="text"
                    value={this.state.filterText}
                    onChange={(e) => this.onChange(e)}
                  />
                </li>
                <br />
              </ul>
            </div>
            <div class="sect">
              <p class="opt1">Sort by</p>
              <ul>
                <li>New Arrivals</li>
                <li>Most Popular</li>
                <li>Lowest Price</li>
                <li>Highest Price</li>
                <li></li>
              </ul>
            </div>
            <br />
            <div class="sect1">
              <p class="opt1">Products</p>
              <ul>
                <li onClick={this.myFilter("Base")}>All</li>
                <li onClick={this.myFilter("Tee")}>Shirts</li>
                <li onClick={this.myFilter("Hoodie")}>Hoodies</li>
                <li onClick={this.myFilter("Shorts")}>Shorts</li>
                <li onClick={this.myFilter("Sweatpants")}>Sweatpants</li>
                <li></li>
              </ul>
            </div>
            <br />
            <div class="sect2">
              <p class="opt1">Color</p>
              <ul>
                <li>Pink</li>
                <li>Blue</li>
                <li>Gray</li>
                <li>Red</li>
                <li>Green</li>
              </ul>
            </div>
            <br />
          </section>
          <div class="product-list">
            {
            Products
            .filter((props) => {
              if (this.state.filterText === "") {
                return true;
              } else {
                return this.normalizeString(props.name).includes(this.normalizeString(this.state.filterText));
              }
            })

            .filter((record) => {
                if (this.state.type === "Base") {
                  return true;
                } else {
                  return this.state.type === record.category;
                }
              })
            .map((props) => (
                <section>
                  <div class="product-item">
                    <div class="product-img">
                      <img
                        src={require("../images/" + props.imgSrc + ".png")} alt={props.imgSrc}
                      />
                      <button type="button" class="add-to-cart-btn"></button>
                    </div>
                    <div class="product-content">
                      <h3 class="product-name">{props.name}</h3>
                      <p class="product-desc">{props.desc}</p>
                      <p class="product-price">{props.price}</p>
                    </div>
                  </div>
                </section>
              ))}
          </div>
        </div>
      </section>
    );
  }
}
