import React from 'react';
import productList from '../products.json'

export default class searchBar extends React.Component {
  state = {
    search: '',
    items: [productList.name ]
  };

  handleChange = (event) => {
    this.setState({ search: event.target.value });
  }

  render() {
    const filteredItems = this.state.items.filter(item => item.name.includes(this.state.search));

    return (
      <div>
        <input
          type="text"
          placeholder="Search..."
          value={this.state.search}
          onChange={this.handleChange}
        />

        <ul>
          {filteredItems.map((item, index) => (
           <main class="cards-container">
           {products.map((props) => { 
               return <section class="card">
               <div class="card-image">
                 <img src={require('../images/' + props.image + '.png')} alt={props.image} />
                 </div> 
                <div class="card-title">
                    <h1>{props.name}</h1>
                </div>
                <div class="card-desc">
                    <hr />
                    <p class="desc">{props.desc}</p>
                </div>
                <label><b>Price: </b>{props.price}</label>
                <div class="btn-2">Add to cart</div>
               </section>   
           })}
           </main>
          ))}
        </ul>
      </div>
    );
  }
}
