import React from 'react'
import '../App.css'
import SlideshowWithPagination from "react-slideshow-with-pagination";


let CARDS_DETAILS = [
  {
    image:`https://i0.wp.com/maytheray.com/wp-content/uploads/2021/06/LITTLE-BLACK-DRESS-OVERSIZED-PLAID-SHIRT.jpg?resize=640%2C800&ssl=1`,
    title: ''
  },
  {
    image:`https://preview.redd.it/22qsn9mljti81.jpg?width=640&crop=smart&auto=webp&s=5a6cff091c70f6040ae99d1b23de8f67126201bb`,
    title: ''
  },
  {
    image:`https://i.pinimg.com/736x/16/b6/25/16b625edf5daedaad4fd240e78feea18.jpg`,
    title: ''
  },
  {
    image:`https://i.pinimg.com/736x/9c/58/04/9c580496af7fb1deb90d2b91bfcd37dc.jpg`,
    title:''
  },
  {
    image:`https://i.pinimg.com/736x/5b/f9/b8/5bf9b8c3cce8fc4c9beb119a6ae4a508.jpg`,
    title:''
  }
]

const Slider = () => {
  return (
    <section id="slideshowcontainer">
    <div class="imagecontainer">
    <SlideshowWithPagination class="imagecontainer"
      options={CARDS_DETAILS}
      showDots={true}
      numberOfCardsPerScreen={1}
      showOneCardForWidthLower="sm"
      slideshowContainerMaxWidth={false}
    />
    <SlideshowWithPagination class="imagecontainer"
      showNumbers={true}
      showDots={true}
    >
      {CARDS_DETAILS.map((images, index) => (
        <React.Fragment key={index}>
          <img class='slideimage' id='img1'src={images.image} alt='image' />
        </React.Fragment>
      ))}
    </SlideshowWithPagination>
    </div>
    </section>
  )
}

export default Slider
