import products from "../products.json"
import React, {useState} from 'react'



const Products = () => {

    const [searchInput, setSearchInput] = useState("");

    const handleChange = (e) => {
        e.preventDefault();
        setSearchInput(e.target.value);
    }

    if (searchInput.length > 0) {
        products.filter((product) => {
            return product(searchInput);
        })
    }

    return (
        <div>
        <div class="search-container">
        <label class="searchLabel" for="searchBar">Search Item By Name: </label>
        <input 
        id="searchBar" 
        onChange={handleChange} 
        type="text" 
        value={searchInput} />
        <br/>
        </div>
        <main class="cards-container">
        {products.map((props) => { 
            return <section class="card">
            <div class="card-image">
              <img src={require('../images/' + props.image + '.png')} alt={props.image} />
              </div> 
             <div class="card-title">
                 <h1>{props.name}</h1>
             </div>
             <div class="card-desc">
                 <hr />
                 <p class="desc">{props.desc}</p>
             </div>
             <label><b>Price: </b>{props.price}</label>
             <div class="btn-2">Add to cart</div>
            </section>   
        })}
        </main>
    </div>
    )
}

export default Products
