import React from "react";
import productList from '../products.json'
import Product from "./Product";

const Products = () => {
    return (
        <div>
        <div class="search-container">
        <label class="searchLabel" for="searchBar">Search Item By Name: </label>
        <input id="searchBar" name="searchBar" onkeyup="searchProduct()" type="text" />
        <br/>
        </div>
        <main class="cards-container">
        {productList.map((data, key) => { 
            return <Product  key={key} image={data.image} name={data.name} desc={data.desc} price={data.price}  />
        })}
        </main>
    </div>
    )
}

export default Products
