import React from 'react';
import productList from '../products.json'


export default class SearchBar extends React.Component {
  state = {
    search: '',
    items: [productList]
  };

  handleChange = (event) => {
    this.setState({ search: event.target.value });
  }

  render() {
    const filteredItems = this.state.items.filter(item => item.name.includes(this.state.search));
    return (
      <div>
        <input
          type="text"
          placeholder="Search..."
          value={this.state.search}
          onChange={this.handleChange}
        />
          {filteredItems.map((item) => (
           <main class="cards-container">
               return <section class="card">
               <div class="card-image">
                 <img src={require('../images/' + item.image + '.png')} alt={item.image} />
                 </div> 
                <div class="card-title">
                    <h1>{item.name}</h1>
                </div>
                <div class="card-desc">
                    <hr />
                    <p class="desc">{item.desc}</p>
                </div>
                <label><b>Price: </b>{item.price}</label>
                <div class="btn-2">Add to cart</div>
               </section>   
           </main>
          ))}
      </div>
    );
  }
}
