import React from "react";
import productList from '../products.json'

export default class Productlist extends React.Component {
    state = {
        products: [],
        type: "All",
        filterText: ""
    }

    //UseState is a react hook that lets you set some state to react component.
    componentDidMount() {
        fetch(productList)
        .then(res => res.json)
        .then(data => {
            this.setState({products: data.data})
        })
    }

    myFilter (type){
        return () => {
            this.setState({type})
        }
    }
    onChange (e){
        this.setState({filterText: e.target.value})

    }

    normalizeString(str) {
        return str.toLowerCase().replace(/\s/g, "")
    }
    render() {

        return (

            <div>
                <h6>Filter By Category: </h6>
                <div id="fButtons">
                    <button id="button" className="btn btn-light" onClick={this.myFilter("All")}>All</button>
                    <button id="button" className="btn btn-light" onClick={this.myFilter("gaming")}>Gaming</button>
                    <button id="button" className="btn btn-light" onClick={this.myFilter("studio")}>Studio</button>
                    <button id="button" className="btn btn-light" onClick={this.myFilter("everyday")}>Everyday</button>
                </div>
                <div className="search-container">
                <h6>Search By Name:</h6>
                <div className="searchBar"id="fButtons">
                    <input className="searchBox" type="text" value={this.state.filterText} onChange={(e) => this.onChange(e)}></input>
                    {/* <button id="button" className="btn btn-light" onClick={() => this.resetFilterText()}>Reset</button> */}
                </div>
                </div>
                <div id="cardAlign" className="row">
                    {
                        this.state.products
                        .filter((props) => {
                            if (this.state.filterText === ""){
                                return true
                            } else{
                                return this.normalizeString(props.Title).includes(this.normalizeString(this.state.filterText))
                            }
                        })

                        .filter((record) => {
                            if (this.state.type === "All"){
                                return true 
                            } else {
                                return this.state.type === record.Category
                            }
                        }).map(props => (

                          <section class="card">
            <div class="card-image">
              <img src={require('../images/' + props.image + '.png')} alt={props.image} />
              </div> 
             <div class="card-title">
                 <h1>{props.name}</h1>
             </div>
             <div class="card-desc">
                 <hr />
                 <p class="desc">{props.desc}</p>
             </div>
             <label><b>Price: </b>{props.price}</label>
             <div class="btn-2">Add to cart</div>
            </section>   
                        ))
                    }
                </div>
            </div>
        )
    }
}
